import React from 'react'
import MenuListItem from '../MenuListItem/MenuListItem'
import './MenuList.css'

export default function MenuList(props) {
    const menus=["좋아요!","정말 좋아요!","최고예요!","미쳤어요!!"]

    return (
        <ul className='container-list'>
            {menus.map((moodEl)=>{
                // mood : 기분 텍스트
                // isSelected : true, false 현재 사용자의 기분이 맞는지 전달
                // onItemClick : setCurrentMood 전달 (현재 기분을 변경하는 함수)
                return <MenuListItem mood={moodEl} isSelected={props.mood === moodEl} onClickEvt={props.onItemClick}/>
            })}
        </ul>
    )
}
