import './App.css';

import Menu from './components/menu/Menu.jsx'
import NewMenu from './components/newMenu/NewMenu.jsx'

// function App() {

//   return (
//     <div>
//       {/**jsx안의 주석이다 */}
//       <h1>안녕 라이캣!</h1>
//       <h1>안녕 라이캣 2호!</h1>
//     </div>
//   );
// }

function TextArea() {
	return (
	  <div class="wrapper">
		<textarea
		  readonly
		  maxlength={3}
		  style={"background-color: blue;"}
		/>
	  </div>
	);   
  }

function App() {
	//여기는 js할 때와 똑같이 주석을 입력합니다! 왜냐면 여기는 JS의 영역이니까요 :)
	return (

		<div>
		{/* 여기부터는 jsx! 주석은 이런식으로 작성합니다~ 한 줄 주석은 따로 존재하지 않습니다. div 밖에 쓰면 오류가 나네?*/}

      	{/* 최상위 태그는 하나만! */}
			<h1>안녕, 라이캣 1호</h1>
			<h1>
				안녕, 라이캣 2호!
			</h1>{/* 태그는 무조건 닫아줘야합니다! 안그러면 에러나와요! */}
			<div/>{/* 이런식으로 바로 태그를 닫히게 할 수도 있어요! */}
		<Textarea></Textarea>
		<Menu></Menu>
		<NewMenu></NewMenu>
		</div>	
	);
}

export default App;
